sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    // Check if the form is loading
    if (isLoading) {
        // If it's not a new record, retain the current value in 'u_what_needed'
        if (!g_form.isNewRecord()) {
            var currentWhatNeeded = g_form.getValue('u_what_needed'); // Get the existing value
            return; // Exit the script so we don't change the options on form load
        }
    }

    // Clear all options from the 'u_what_needed' field
    g_form.clearOptions('u_what_needed');

    // Check the new value (Request Type) and add corresponding options
    if (newValue === 'hr') {
        g_form.addOption('u_what_needed', 'hr1', 'Human Resources 1');
        g_form.addOption('u_what_needed', 'hr2', 'Human Resources 2');
    } else if (newValue === 'facilities') {
        g_form.addOption('u_what_needed', 'fac1', 'Facilities 1');
        g_form.addOption('u_what_needed', 'fac2', 'Facilities 2');
    } else if (newValue === 'legal') {
        g_form.addOption('u_what_needed', 'leg1', 'Legal 1');
        g_form.addOption('u_what_needed', 'leg2', 'Legal 2');
    }

    // Add the "Other" option for all Request Types
    g_form.addOption('u_what_needed', 'other', 'Other');

    // If not a new record, set the What Needed field to its previous value
    if (!g_form.isNewRecord() && currentWhatNeeded) {
        g_form.setValue('u_what_needed', currentWhatNeeded); // Restore previous value
    }
}
